import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setProductData } from './redux/productSlice';
import jsonData from './stackline_frontend_assessment_data_2021.json'; // Adjust the path to where your JSON file is located


// Import the components
import Header from './components/Header';
import ProductDetails from './components/ProductDetails';
import SalesGraph from './components/SalesGraph';
import SalesTable from './components/SalesTable';

function App() {
  const dispatch = useDispatch();
  const productData = useSelector(state => state.product.data); // Accessing the product data from the Redux store

  useEffect(() => {
    dispatch(setProductData(jsonData));
    console.log('Product Data Loaded:', productData); // This will log the current state of product data
  }, [dispatch, productData]);

  return (
     <div>
      <Header />
      {productData && (
        <>
          <ProductDetails />
          <SalesGraph />
          <SalesTable />
        </>
      )}
    </div>
  );
}

export default App;
